import ipaddress
def ipfilter(ip):
    ranges =  ['192.168.0.0/24',]
    for item in ranges:
        if phantom.is_ip(ip) and ipaddress.ip_address(unicode(ip)) not in ipaddress.ip_network(unicode(item)):
            return ip
    return None

