    parameters = []

    # build parameters list for 'hunt_file_1' call
    for wannacry_hash in cl_wannacry_hashes:
        if wannacry_hash:
            parameters.append({
                'hash': wannacry_hash,
                'range': "",
                'type': "",
            })

    phantom.act("hunt file", parameters=parameters, assets=['carbonblack'], callback=get_system_info_1, name="hunt_file_1")    

    return
